import { useState } from "react";
import axios from "axios";
function Home() {
    const axios = require('axios').default;
    const[name,setName]=useState()
    const[socialId,setSocialId]=useState()
    const[img,setimg]=useState()
    const apiUrl='https://swd-app-e2c0f-default-rtdb.firebaseio.com//Users.json'
    const onSubmitHandler=(e)=>{
        e.preventDefault()
        console.log(img)
        axios.post(apiUrl,{Name : name ,  ID : socialId , img : img.name})
        setSocialId('')
        setName('')
        setimg('')

    }
    const onChangeNameHandler=(e)=>{
       setName(e.target.value)
    }
    const onChangeSocialIDHandler=(e)=>{
        setSocialId(e.target.value)
     }
     const onChangePhotoHandler=(e)=>{
        setimg(e.target.files[0])
     }
     
  return (
      
      <form className='container mt-5 w-50' onSubmit={onSubmitHandler}>
        <div className="form-group">
          <label htmlFor="exampleInput1" >Name</label>
          <input required 
            type="text"
            onChange={onChangeNameHandler}
            className="form-control"
            id="exampleInput1"
            aria-describedby="emailHelp"
            placeholder="Enter email"
            value={name}
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInput2">Social ID</label>
          <input
          min="1"
          value={socialId}
          onChange={onChangeSocialIDHandler}
            type="number"
            required
            className="form-control"
            id="exampleInput2"
            placeholder="Social ID"
          />
        </div>
        <div className="form-group">
          <label htmlFor="exampleInput3">Photo</label>
          <input
          required
          onChange={onChangePhotoHandler}
          type="file"
          className="form-control"
          id="exampleInput3"
          placeholder="Password"
          accept="image/*"
          files={img}
  />
        </div>
       
        <button type="submit" className="btn btn-dark mt-2 w-100 ">
          Submit
        </button>
      </form>
  );
}

export default Home;
