import classes from "./Admin.module.css";
import axios from "axios";
import { useEffect, useState } from "react";
function Admin() {
  const [arr , setArr ] = useState(null)
  const apiUrl='https://swd-app-e2c0f-default-rtdb.firebaseio.com//Users.json'

  useEffect(()=>{
    axios.get(apiUrl).then(res=>{
      console.log(res.data)
      const users = []
      for ( const key in res.data){
        users.push({
          iD : key,
          SocialID: res.data[key].ID,
          Name : res.data[key].Name,
          img : res.data[key].img
        })
        
      } 
      setArr(users)
      
    }
    )
  },[])


  ////content management//// 
  var content = ""
  if(arr == [] || arr == null){
    content = <h1>There is no data </h1>
  }else{
    content =  <table class="table">
    <thead>
      <tr>
        <th scope="col">#</th>
        <th scope="col">Name</th>
        <th scope="col">SocialId</th>
        <th scope="col">Image url</th>
      </tr>
    </thead>
    <tbody>
     {arr.map(user => (
       <tr>
         <td>{arr.indexOf(user)+1}</td>
         <td>{user.Name}</td>
         <td>{user.SocialID}</td>
         <td><a href={"#"}>{user.img}</a></td>
       </tr>
     ))}
    </tbody>
  </table>
  }
  return (
    <div className="container w-60 mt-3 text-center">
     {content}
    </div>
  );
}

export default Admin;
